import './App.css';
import {Button, Col, Form, FormControl, FormGroup, Row} from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import {useState} from "react";

const handleFocus = (event) => event.target.select();

const handleCopyClick = () => {
    // Asynchronously call copyTextToClipboard
    copyTextToClipboard(copyText)
        .then(() => {
            // If successful, update the isCopied state value
            setIsCopied(true);
            setTimeout(() => {
                setIsCopied(false);
            }, 1500);
        })
        .catch((err) => {
            console.log(err);
        });
}

const CopyArea = (props) => {
    return (
        <div className='url-form'>
            <Form.Control
                onFocus={handleFocus}
                type="text"
                value={props.copyText}
                readOnly
            />
            <Button className="btn-light">
                <span>{props.isCopied ? 'Copied!' : 'Copy'}</span>
            </Button>
        </div>
    )
}

function App() {
    const [isCopied, setIsCopied] = useState(false);
    const [copyText, setCopyText] = useState('test text');

    return (
        <div className="App">
            <div className="Content">
                <p>Enter a url to shorten</p>
                <Form className='url-form'>
                    <Form.Control
                        id="url"
                        placeholder="https://yoururl.com/"
                    />
                    <Button
                        type="submit"
                        className="btn-light"
                    >
                        Shorten
                    </Button>
                </Form>
                <div className="shortened-link-area">
                    <CopyArea copyText={copyText} isCopied={isCopied} />
                    <a
                        className="Link"
                        href="#"
                        target="_blank"
                        rel="noopener noreferrer"
                    >
                        https://smlr.org/yourlink
                    </a>
                </div>
            </div>
        </div>
    );
}

export default App;
