import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Button, Form} from "react-bootstrap";
import {useState} from "react";

const ResponseURLArea = ({copyText, originalURL}) => {
    const [copied, setCopied] = useState(false);

    const handleFocus = (event) => event.target.select();

    const handleCopyClick = () => {
        copyTextToClipboard(copyText)
            .then(() => {
                setCopied(true);
                setTimeout(() => {
                    setCopied(false);
                }, 1500);
            })
            .catch((err) => {
                console.log(err);
            });
    }

    async function copyTextToClipboard(text) {
        if ('clipboard' in navigator) {
            return await navigator.clipboard.writeText(text);
        } else {
            return document.execCommand('copy', true, text);
        }
    }

    return (
        <>
            <div className='divider' />
            <div>
                <b>Your shortened URL</b>
                <p>(originally {originalURL})</p>
                <div className='url-form'>
                    <Form.Control
                        onFocus={handleFocus}
                        type="text"
                        value={copyText}
                        readOnly
                    />
                    <Button className="btn-light" onClick={handleCopyClick}>
                        <span>{copied ? 'Copied' : 'Copy'}</span>
                    </Button>
                </div>
                <a
                    className="Link"
                    href="#"
                    target="_blank"
                    rel="noopener noreferrer"
                >
                    https://smlr.org/yourlink
                </a>
            </div>
        </>
    )
}

function App() {
    return (
        <div className="App">
            <div className="Content">
                <div>
                    <b>smlr.org</b>
                    <p>Enter a URL and a desired link lifespan</p>
                    <Form className='url-form'>
                        <Form.Control
                            id="url"
                            placeholder="https://yoururl.com/"
                        />
                        <Form.Select aria-label="Default select example">
                            <option value="0">Forever</option>
                            <option value="1">1 day</option>
                            <option value="2">1 week</option>
                            <option value="3">1 month</option>
                            <option value="4">6 months</option>
                            <option value="5">1 year</option>
                        </Form.Select>
                        <Button
                            type="submit"
                            className="btn-light"
                        >
                            Shorten
                        </Button>
                    </Form>
                </div>
                <ResponseURLArea
                    copyText={"test"}
                    originalURL={"https://test.com/"}
                />
            </div>
        </div>
    );
}

export default App;
