import './App.css';
import {Button, Col, Form, FormControl, FormGroup, Row} from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import {useState} from "react";

const handleFocus = (event) => event.target.select();

const CopyArea = ({copyText}) => {
    const [isCopied, setIsCopied] = useState(false);

    const handleCopyClick = () => {
        // Asynchronously call copyTextToClipboard
        copyTextToClipboard(copyText)
            .then(() => {
                // If successful, update the isCopied state value
                setIsCopied(true);
                setTimeout(() => {
                    setIsCopied(false);
                }, 1500);
            })
            .catch((err) => {
                console.log(err);
            });
    }

    async function copyTextToClipboard(text) {
        if ('clipboard' in navigator) {
            return await navigator.clipboard.writeText(text);
        } else {
            return document.execCommand('copy', true, text);
        }
    }

    return (
        <div className='url-form'>
            <Form.Control
                onFocus={handleFocus}
                type="text"
                value={copyText}
                readOnly
            />
            <Button className="btn-light">
                <span>{isCopied ? 'Copied' : 'Copy'}</span>
            </Button>
        </div>
    )
}

function App() {
    return (
        <div className="App">
            <div className="Content">
                <p>Enter a url to shorten</p>
                <Form className='url-form'>
                    <Form.Control
                        id="url"
                        placeholder="https://yoururl.com/"
                    />
                    <Button
                        type="submit"
                        className="btn-light"
                    >
                        Shorten
                    </Button>
                </Form>
                <div className="shortened-link-area">
                    <CopyArea />
                    <a
                        className="Link"
                        href="#"
                        target="_blank"
                        rel="noopener noreferrer"
                    >
                        https://smlr.org/yourlink
                    </a>
                </div>
            </div>
        </div>
    );
}

export default App;
