import './App.css';
import {Button, Col, Form, FormControl, FormGroup, Row} from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import {useState} from "react";

const handleFocus = (event) => event.target.select();
const Input = (props) => <Form.Control type="text" value="Some something" onFocus={handleFocus} />


function App() {
    const [isCopied, setIsCopied] = useState(false);
    const [copyText, setCopyText] = useState('test text');

    const URLForm = () => {
        return (
            <div className='url-form'>
                <Form.Control
                    type="text"
                    value={copyText}
                    readOnly
                />
                <Button className="btn-light">
                    <span>{isCopied ? 'Copied!' : 'Copy'}</span>
                </Button>
            </div>
        )
    }

    return (
        <div className="App">
            <div className="Content">
                <p>Enter a url to shorten</p>
                <Form className='url-form'>
                    <Form.Control
                        id="url"
                        placeholder="https://yoururl.com/"
                    />
                    <Button
                        type="submit"
                        className="btn-light"
                    >
                        Shorten
                    </Button>
                </Form>
                <div className="shortened-link-area">
                    <URLForm />
                    <a
                        className="Link"
                        href="#"
                        target="_blank"
                        rel="noopener noreferrer"
                    >
                        https://smlr.org/yourlink
                    </a>
                </div>
            </div>
        </div>
    );
}

export default App;
